import React, { useState } from 'react';
import './FormPage.css';

const FormPage = () => {
  const [formData, setFormData] = useState({
    websiteType: '',
    businessLocation: '',
    businessName: '',
    additionalInfo: '',
  });

  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [isDragging, setIsDragging] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted with data: ', formData, 'Uploaded Files: ', uploadedFiles);
    // Handle form submission (e.g., send data to an API)
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();

    setIsDragging(false);

    if (e.dataTransfer.files && e.dataTransfer.files.length > 0) {
      const files = Array.from(e.dataTransfer.files);
      setUploadedFiles((prevFiles) => [...prevFiles, ...files]);

      setFormData((prevData) => ({
        ...prevData,
        additionalInfo: `${prevData.additionalInfo}\nUploaded ${files.map((file) => file.name).join(', ')}`,
      }));

      e.dataTransfer.clearData();
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);
  };

  return (
    <div className="form-page">
      <h2>Generate Your Website</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="websiteType">What kind of website do you want to build?</label>
          <input
            type="text"
            id="websiteType"
            name="websiteType"
            value={formData.websiteType}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="businessLocation">What is the location of your business?</label>
          <input
            type="text"
            id="businessLocation"
            name="businessLocation"
            value={formData.businessLocation}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="businessName">What is the name of your business?</label>
          <input
            type="text"
            id="businessName"
            name="businessName"
            value={formData.businessName}
            onChange={handleChange}
            required
          />
        </div>
        <div
          className={`textarea-container ${isDragging ? 'dragging' : ''}`}
          onDrop={handleDrop}
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
        >
          <label htmlFor="additionalInfo">Any additional information?</label>
          <textarea
            id="additionalInfo"
            name="additionalInfo"
            value={formData.additionalInfo}
            onChange={handleChange}
            placeholder="Drag and drop files here or type your message..."
          ></textarea>
          <div className="uploaded-files">
            {uploadedFiles.map((file, index) => (
              <div key={index} className="file-icon">
                <img
                  src="/file-icon.png" // Replace with your file icon path
                  alt="File Icon"
                  className="file-thumbnail"
                />
                <span className="file-name">{file.name}</span>
              </div>
            ))}
          </div>
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default FormPage;
