import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom'; // Import necessary React Router components
import Carousel from './components/Carousel/Carousel'; // Import the Carousel component
import FormPage from './components/FormPage'; // Import the FormPage component

const App = () => {
  return (
    <Router> {/* Wrap your whole application with Router for routing */}
      <div className="App">
        <header>
          <figure className="logo">
            <img src="images/logo.png" alt="Logo" />
          </figure>
          <nav className="main-nav">
            <ul>
              <li><a href="">Coffee</a></li>
              <li><a href="">Menu</a></li>
              <li><a href="">About</a></li>
            </ul>
          </nav>
        </header>

        <main>
          <Carousel /> {/* Carousel component will be rendered here */}
          {/* If you want the Start Building button to navigate to the form page, update it to use React Router's Link or `useNavigate` */}
          <Link to="/form"> {/* Link to navigate to the form page */}
            <button className="start-build-btn">
              Start Building
            </button>
          </Link>
        </main>

        <Routes>
          {/* Define routes for your application */}
          <Route path="/" element={<Carousel />} /> {/* Route for Carousel */}
          <Route path="/form" element={<FormPage />} /> {/* Route for FormPage */}
        </Routes>
      </div>
    </Router>
  );
};

export default App;
