import React, { useState, useEffect } from 'react';
import "./ImageSlider.css";
import nasaImage from '../assets/nasa.jpg'; // Adjust the relative path based on where this file is located

const ImageSlider = () => {
  const [activeIndex, setActiveIndex] = useState(3); // Start with the 4th image (index 3)

  const images = [
    { src: nasa, name: 'E-commerce', role: 'Web3 Developer' },
    { src: 'assets/nasa.jpg', name: 'Blog', role: 'WordPress Developer' },
    { src: 'assets/nasa.jpg', name: 'Portfolio', role: 'Java Developer' },
    { src: 'assets/nasa.jpg', name: 'Business', role: 'Web Developer' },
    { src: 'assets/nasa.jpg', name: 'Education', role: 'PHP Developer' },
    { src: 'assets/nasa.jpg', name: 'Ryan', role: 'SEO Developer' },
    { src: 'assets/nasa.jpg', name: 'Dakes', role: 'SQL Developer' },
  ];

  const handleImageClick = (index) => {
    setActiveIndex(index);
  };

  useEffect(() => {
    // Get the slider container and the active image
    const sliderImages = document.querySelector('.slider-images');
    const activeImage = document.querySelector('.slider-img.active');

    // Scroll the slider horizontally to bring the active image into view
    if (activeImage) {
      const offset = activeImage.offsetLeft - (window.innerWidth / 2 - activeImage.offsetWidth / 2);
      sliderImages.style.transform = `translateX(-${offset}px)`;
    }
  }, [activeIndex]);

  return (
    <section className="slider-container">
      <div className="slider-images">
        {images.map((image, index) => (
          <div
            key={index}
            className={`slider-img ${index === activeIndex ? 'active' : ''}`}
            onClick={() => handleImageClick(index)}
          >
            <img src={image.src} alt={image.name} />
            <h1>{image.name}</h1>
            <div className="details">
              <h2>{image.name}</h2>
              <p>{image.role}</p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default ImageSlider;
