import React, { useState } from 'react';
import { SketchPicker } from 'react-color'; // Import the SketchPicker component
import './DesignPage.css';

const DesignPage = () => {
  const [selectedColor, setSelectedColor] = useState('#000000'); // Default color
  const [isPickerOpen, setIsPickerOpen] = useState(false); // Toggle for color picker
  const [selectedLayout, setSelectedLayout] = useState('');
  const [customLayout, setCustomLayout] = useState(''); // For storing custom layout input

  const layoutOptions = ['Travel', 'Fitness', 'Business', 'Portfolio', 'E-Commerce', 'Other'];

  const handleColorChange = (color) => {
    setSelectedColor(color.hex);
  };

  const handleLayoutChange = (e) => {
    const value = e.target.value;
    setSelectedLayout(value);

    if (value === 'Other') {
      const userInput = prompt('Please provide details for your custom layout:');
      if (userInput) {
        setCustomLayout(userInput);
      } else {
        setSelectedLayout(''); // Reset to prevent empty input
      }
    } else {
      setCustomLayout(''); // Clear custom layout if not "Other"
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Selected Color:', selectedColor);
    console.log('Selected Layout:', selectedLayout === 'Other' ? customLayout : selectedLayout);

    // Redirect or handle submission logic
    alert(`Color: ${selectedColor}, Layout: ${selectedLayout === 'Other' ? customLayout : selectedLayout}`);
  };

  return (
    <div className="design-page full-page">
      <h2>Customize Your Website Design</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-section">
          <label htmlFor="colorPalette">Choose a color:</label>
          <div className="color-picker-container">
            <button
              type="button"
              className="color-picker-icon"
              onClick={() => setIsPickerOpen(!isPickerOpen)}
              style={{ backgroundColor: selectedColor }}
            ></button>
            {isPickerOpen && (
              <div className="color-picker-popover">
                <div className="color-picker-cover" onClick={() => setIsPickerOpen(false)} />
                <SketchPicker
                  color={selectedColor}
                  onChangeComplete={handleColorChange}
                />
              </div>
            )}
          </div>
          <p>
            Selected Color: <span style={{ color: selectedColor }}>{selectedColor}</span>
          </p>
        </div>

        <div className="form-section">
          <label htmlFor="layoutTemplate">Choose a layout template:</label>
          <select
            id="layoutTemplate"
            value={selectedLayout}
            onChange={handleLayoutChange}
          >
            <option value="">Select a layout</option>
            {layoutOptions.map((layout, index) => (
              <option key={index} value={layout}>
                {layout}
              </option>
            ))}
          </select>
          {selectedLayout === 'Other' && customLayout && (
            <p>Custom Layout: {customLayout}</p>
          )}
        </div>

        <button type="submit" disabled={!selectedLayout}>
          Generate Website
        </button>
      </form>
    </div>
  );
};

export default DesignPage;
