import React, { useState } from 'react';
import './DesignPage.css';

const DesignPage = () => {
  const [selectedColor, setSelectedColor] = useState('');
  const [selectedLayout, setSelectedLayout] = useState('');

  const colorOptions = ['#FF5733', '#4F8B69', '#3498DB', '#F4D03F', '#7D3C98'];
  const layoutOptions = ['Grid Layout', 'Minimalist Layout', 'Portfolio Layout', 'E-Commerce Layout'];

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Selected Color:', selectedColor);
    console.log('Selected Layout:', selectedLayout);

    // Redirect or handle form submission logic
    alert(`Color: ${selectedColor}, Layout: ${selectedLayout}`);
  };

  return (
    <div className="design-page">
      <h2>Customize Your Website Design</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-section">
          <label htmlFor="colorPalette">Choose a color palette:</label>
          <div className="color-options">
            {colorOptions.map((color, index) => (
              <div
                key={index}
                className={`color-option ${selectedColor === color ? 'selected' : ''}`}
                style={{ backgroundColor: color }}
                onClick={() => setSelectedColor(color)}
              ></div>
            ))}
          </div>
        </div>

        <div className="form-section">
          <label htmlFor="layoutTemplate">Choose a layout template:</label>
          <select
            id="layoutTemplate"
            value={selectedLayout}
            onChange={(e) => setSelectedLayout(e.target.value)}
          >
            <option value="">Select a layout</option>
            {layoutOptions.map((layout, index) => (
              <option key={index} value={layout}>
                {layout}
              </option>
            ))}
          </select>
        </div>

        <button type="submit" disabled={!selectedColor || !selectedLayout}>
          Generate Website
        </button>
      </form>
    </div>
  );
};

export default DesignPage;
