import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';  // Import useNavigate
import './Carousel.css';  // Import your CSS here

const Carousel = () => {
  const [active, setActive] = useState(0);  // Start with the first slide
  const navigate = useNavigate(); // Initialize useNavigate hook

  const items = [
    {
      title: 'AI that builds a website for you',
      price: 'Travel',
      description: 'Lorem ipsum dolor sit amet consectetur adipisicing elit.',
      imgSrc: '/images/travelv.jpg',
      bgColor: '#f2dad3',
    },
    {
      title: 'AI that builds a website for you',
      price: 'Business',
      description: 'Lorem ipsum dolor sit amet consectetur adipisicing elit.',
      imgSrc: '/images/businessv.jpg',
      bgColor: '#f5bfaf',
    },
    {
      title: 'AI that builds a website for you',
      price: 'E-Commerce',
      description: 'Lorem ipsum dolor sit amet consectetur adipisicing elit.',
      imgSrc: '/images/ecommv.png',
      bgColor: '#dedfe1',
    },
    {
      title: 'AI that builds a website for you',
      price: 'Portfolio',
      description: 'Lorem ipsum dolor sit amet consectetur adipisicing elit.',
      imgSrc: '/images/portfoliov.jpg',
      bgColor: '#7eb63d',
    },
  ];

  const countItem = items.length;

  useEffect(() => {
    const interval = setInterval(() => {
      setActive((prevActive) => (prevActive + 1) % countItem);  // Simple auto slide logic
    }, 5000);
    return () => clearInterval(interval);
  }, [active]);

  const handleGenerateWebsite = () => {
    navigate('/form'); // Navigate to the form page
  };

  return (
    <section className={`carousel ${active === 0 ? 'next' : ''}`}>
      <div className="list">
        {items.map((item, index) => (
          <article
            key={index}
            className={`item ${index === active ? 'active' : ''}`}
            style={{ backgroundColor: item.bgColor }}
          >
            <div className="main-content">
              <div className="content">
                <h2>{item.title}</h2>
                <p className="price">{item.price}</p>
                <p className="description">{item.description}</p>
                <button className="addToCard" onClick={handleGenerateWebsite}>
                  Generate your website
                </button>
              </div>
            </div>
            <figure className="image">
              <img src={item.imgSrc} alt={item.title} />
            </figure>
          </article>
        ))}
      </div>
      <div className="arrows">
        <button id="prev" onClick={() => setActive((active - 1 + countItem) % countItem)}>
          &lt;
        </button>
        <button id="next" onClick={() => setActive((active + 1) % countItem)}>
          &gt;
        </button>
      </div>
    </section>
  );
};

export default Carousel;
