import React, { useState } from 'react';
import './FormPage.css'; // Make sure to import the CSS file

const FormPage = () => {
  const [formData, setFormData] = useState({
    websiteType: '',
    businessLocation: '',
    businessName: '',
    additionalInfo: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted with data: ', formData);
    // Handle form submission (e.g., send data to an API)
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    const droppedData = e.dataTransfer.getData('text') || e.dataTransfer.files[0]?.name;
    setFormData((prevData) => ({
      ...prevData,
      additionalInfo: prevData.additionalInfo + (droppedData ? `\n${droppedData}` : ''),
    }));
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.stopPropagation();
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    e.stopPropagation();
  };

  return (
    <div className="form-page">
      <h2>Generate Your Website</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="websiteType">What kind of website do you want to build?</label>
          <input
            type="text"
            id="websiteType"
            name="websiteType"
            value={formData.websiteType}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="businessLocation">What is the location of your business?</label>
          <input
            type="text"
            id="businessLocation"
            name="businessLocation"
            value={formData.businessLocation}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="businessName">What is the name of your business?</label>
          <input
            type="text"
            id="businessName"
            name="businessName"
            value={formData.businessName}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="additionalInfo">Any additional information?</label>
          <textarea
            id="additionalInfo"
            name="additionalInfo"
            value={formData.additionalInfo}
            onChange={handleChange}
            onDrop={handleDrop}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            placeholder="Drag and drop text or files here..."
          ></textarea>
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default FormPage;
