// src/components/Carousel/Carousel.js

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';  // Import useNavigate hook
import './Carousel.css';

const Carousel = () => {
  const [active, setActive] = useState(1);
  const [other1, setOther1] = useState(null);
  const [other2, setOther2] = useState(null);
  const [autoPlay, setAutoPlay] = useState(null);

  const navigate = useNavigate();  // Initialize navigate


  const items = [
    {
      title: 'AI that builds a website for you',
      price: 'Travel',
      description: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Asperiores labore animi voluptatibus sequi illo...',
      imgSrc: '/images/travelv.jpg',
      bgColor: '#f2dad3',
    },
    {
      title: 'AI that builds a website for you',
      price: 'Business',
      description: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Asperiores labore animi voluptatibus sequi illo...',
      imgSrc: '/images/businessv.jpg',
      bgColor: '#f5bfaf',
    },
    {
      title: 'AI that builds a website for you',
      desc: 'E-Commerce',
      price: 'E-Commerce',
      description: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Asperiores labore animi voluptatibus sequi illo...',
      imgSrc: '/images/ecommv.png',
      bgColor: '#dedfe1',
    },
    {
      title: 'AI that builds a website for you',
      desc: 'Portfolio',
      price: 'Portfolio',
      description: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Asperiores labore animi voluptatibus sequi illo...',
      imgSrc: '/images/portfoliov.jpg',
      bgColor: '#7eb63d',
    },
  ];

  const countItem = items.length;

  // Carousel transitions
  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 5000);
    return () => clearInterval(interval);
  }, [active]);

  const updateActiveItems = (newActive) => {
    const newOther1 = (newActive - 1 + countItem) % countItem;
    const newOther2 = (newActive + 1) % countItem;
    setActive(newActive);
    setOther1(newOther1);
    setOther2(newOther2);
  };

  const nextSlide = () => {
    const newActive = (active + 1) % countItem;
    updateActiveItems(newActive);
  };

  const prevSlide = () => {
    const newActive = (active - 1 + countItem) % countItem;
    updateActiveItems(newActive);
  };

  const handleButtonClick = () => {
    navigate('/form');  // Navigate to the form page when the button is clicked
  };

  return (
    <section className={`carousel ${active === 0 ? 'next' : ''}`}>
      <div className="list">
        {items.map((item, index) => (
          <article
            key={index}
            className={`item ${index === active ? 'active' : ''} ${
              index === other1 ? 'other_1' : ''
            } ${index === other2 ? 'other_2' : ''}`}
          >
            <div className="main-content" style={{ backgroundColor: item.bgColor }}>
              <div className="content">
                <h2>{item.title}</h2>
                <p className="price">{item.price}</p>
                <p className="description">{item.description}</p>
                <button className="addToCard" onClick={handleButtonClick}>Generate your website</button> {/* Button click to navigate */}
              </div>
            </div>
            <figure className="image">
              <img src={item.imgSrc} alt={item.title} />
            </figure>
          </article>
        ))}
      </div>
      <div className="arrows">
        <button id="prev" onClick={prevSlide}>
          &lt;
        </button>
        <button id="next" onClick={nextSlide}>
          &gt;
        </button>
      </div>
    </section>
  );
};

export default Carousel;
