import React, { useState, useEffect } from 'react';
import '../../jQuery'; // Import the jQuery file (adjust path accordingly)

const ImageSlider = () => {
  const [activeIndex, setActiveIndex] = useState(3); // Start with the 4th image (index 3)

  const images = [
    { src: 'Images/1.png', name: 'Mike', role: 'web3 Developer' },
    { src: 'Images/2.png', name: 'Samite', role: 'WordPress Developer' },
    { src: 'Images/3.png', name: 'Hashi', role: 'Java Developer' },
    { src: 'Images/4.png', name: 'Kaity', role: 'Web Developer' },
    { src: 'Images/5.png', name: 'Lauren', role: 'PHP Developer' },
    { src: 'Images/6.png', name: 'Ryan', role: 'SEO Developer' },
    { src: 'Images/7.png', name: 'Dakes', role: 'SQL Developer' },
  ];

  const handleImageClick = (index) => {
    setActiveIndex(index);
  };

  useEffect(() => {
    // Use the imported jQuery
    if (window.$) {
      // Example: Add hover effect using the uploaded jQuery
      window.$('.slider-img').hover(
        function () {
          window.$(this).css('transform', 'scale(1.2)');
        },
        function () {
          window.$(this).css('transform', 'scale(1)');
        }
      );
    }

    // Cleanup
    return () => {
      if (window.$) {
        window.$('.slider-img').off('mouseenter mouseleave');
      }
    };
  }, []);

  return (
    <section className="slider-container">
      <div className="slider-images">
        {images.map((image, index) => (
          <div
            key={index}
            className={`slider-img ${index === activeIndex ? 'active' : ''}`}
            onClick={() => handleImageClick(index)}
          >
            <img src={image.src} alt={image.name} />
            <h1>{image.name}</h1>
            <div className="details">
              <h2>{image.name}</h2>
              <p>{image.role}</p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default ImageSlider;
