import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './GeneratedWebsite.css';

const GeneratedWebsite = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { layout, palette, font } = location.state || {};

  const [userPrompt, setUserPrompt] = useState('');
  const [generatedHTML, setGeneratedHTML] = useState('');

  const generateWebsite = (prompt) => {
    const newCSS = `
body {
  font-family: ${font || 'Arial'}, sans-serif;
  background-color: ${palette ? palette[0] : '#ffffff'};
  color: ${palette ? palette[1] : '#000000'};
  text-align: center;
}
.container {
  max-width: 1200px;
  margin: auto;
  padding: 20px;
}
.header {
  background-color: ${palette ? palette[2] : '#f0f0f0'};
  padding: 20px;
  font-size: 24px;
  font-weight: bold;
}
.content {
  padding: 20px;
}
.footer {
  background-color: ${palette ? palette[3] : '#e0e0e0'};
  padding: 10px;
  margin-top: 20px;
}
    `;

    const newJS = `
document.addEventListener('DOMContentLoaded', () => {
  const header = document.querySelector('.header');
  header.addEventListener('click', () => {
    alert('Header clicked!');
  });
});
    `;

    const newHTML = `
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Generated ${layout || 'Dynamic'} Website</title>
  <link rel="stylesheet" href="generated.css">
</head>
<body>
  <div class="container">
    <div class="header">${prompt || 'Welcome to the Generated Website'}</div>
    <div class="content">This is a dynamically generated website preview.</div>
    <div class="footer">© 2025 Your Brand</div>
  </div>
  <script src="generated.js"></script>
</body>
</html>
    `;

    // Save the files to separate states
    setGeneratedHTML(newHTML);

    // Save CSS and JS files in the public folder for live previewing
    fetch('/save-generated', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        html: newHTML,
        css: newCSS,
        js: newJS,
      }),
    }).catch((err) => console.error('Error saving files:', err));
  };

  return (
    <div className="generated-website-container">
      <div className="website-preview-container">
        <button
          className="generate-button"
          onClick={() => generateWebsite(userPrompt)}
        >
          Generate Website
        </button>
        <iframe
          title="Website Preview"
          className="website-preview"
          src="/generated.html"
        />
      </div>
      <div className="code-container">
        <h3>HTML Code</h3>
        <pre className="code-block">
          <code>{generatedHTML}</code>
        </pre>
      </div>
    </div>
  );
};

export default GeneratedWebsite;
