// src/components/FormPage.js

import React, { useState } from 'react';

const FormPage = () => {
  const [formData, setFormData] = useState({
    websiteType: '',
    businessLocation: '',
    businessName: '',
    additionalInfo: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted with data: ', formData);
    // Handle form submission (e.g., send data to an API)
  };

  return (
    <div className="form-page">
      <h2>Generate Your Website</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="websiteType">What kind of website do you want to build?</label>
          <input
            type="text"
            id="websiteType"
            name="websiteType"
            value={formData.websiteType}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="businessLocation">What is the location of your business?</label>
          <input
            type="text"
            id="businessLocation"
            name="businessLocation"
            value={formData.businessLocation}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="businessName">What is the name of your business?</label>
          <input
            type="text"
            id="businessName"
            name="businessName"
            value={formData.businessName}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="additionalInfo">Any additional information?</label>
          <textarea
            id="additionalInfo"
            name="additionalInfo"
            value={formData.additionalInfo}
            onChange={handleChange}
          ></textarea>
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default FormPage;
